using NUnit.Framework; using UnityEngine; using UnityEngine.TestTools;  public class PlatformTests {     [Test]     [UnityPlatform(RuntimePlatform.WindowsPlayer)]     public void WindowsPlayer_Test()     {         Assert.AreEqual(Application.platform, RuntimePlatform.WindowsPlayer);     }       [Test]     [UnityPlatform(RuntimePlatform.WindowsEditor)]     public void WindowsEditor_Test()     {         Assert.AreEqual(Application.platform, RuntimePlatform.WindowsEditor);     }      [Test]     [UnityPlatform(RuntimePlatform.OSXPlayer)]     public void MacPlayer_Test()     {         Assert.AreEqual(Application.platform, RuntimePlatform.OSXPlayer);     }      [Test]     [UnityPlatform(RuntimePlatform.OSXEditor)]     public void MacEditor_Test()     {         Assert.AreEqual(Application.platform, RuntimePlatform.OSXEditor);     }      [Test]     [UnityPlatform(RuntimePlatform.Android)]     public void Android_Test()     {         Assert.AreEqual(Application.platform, RuntimePlatform.Android);     }       [Test]     [UnityPlatform(exclude = new[] { RuntimePlatform.IPhonePlayer })]     public void Exclude_Test()     {         Assert.AreNotEqual(Application.platform, RuntimePlatform.IPhonePlayer);     } }   