using NUnit.Framework; using UnityEngine; using UnityEngine.TestTools;  public class PlatformTests {     [Test]     [UnityPlatform(RuntimePlatform.WindowsPlayer)]     public void TestWindowsPlayer()     {         Assert.AreEqual(Application.platform, RuntimePlatform.WindowsPlayer);     }       [Test]     [UnityPlatform(RuntimePlatform.WindowsEditor)]     public void TestWindowsEditor()     {         Assert.AreEqual(Application.platform, RuntimePlatform.WindowsEditor);     }      [Test]     [UnityPlatform(RuntimePlatform.OSXPlayer)]     public void TestMacPlayer()     {         Assert.AreEqual(Application.platform, RuntimePlatform.OSXPlayer);     }      [Test]     [UnityPlatform(RuntimePlatform.OSXEditor)]     public void TestMacEditor()     {         Assert.AreEqual(Application.platform, RuntimePlatform.OSXEditor);     }      [Test]     [UnityPlatform(RuntimePlatform.Android)]     public void TestAndroid()     {         Assert.AreEqual(Application.platform, RuntimePlatform.Android);     }       [Test]     [UnityPlatform(exclude = new[] { RuntimePlatform.IPhonePlayer })]     public void TestExclude()     {         Assert.AreNotEqual(Application.platform, RuntimePlatform.IPhonePlayer);     } }   