using System.Collections; using NUnit.Framework; using UnityEngine; using UnityEngine.TestTools; using UnityEngine.SceneManagement; using NSubstitute;  namespace Tests {     public class PlayModeTests     {          // A Test behaves as an ordinary method         [Test]         public void PlayModeTestsSimplePasses()         {             // Use the Assert class to test conditions         }          // A UnityTest behaves like a coroutine in Play Mode. In Edit Mode you can use         // `yield return null;` to skip a frame.         [UnityTest]         public IEnumerator PlayModeTestsWithEnumeratorPasses()         {             // Use the Assert class to test conditions.             // Use yield to skip a frame.             yield return null;         }          [UnityTest]         public IEnumerator GameObject_WithRigidBody_WillBeAffectedByPhysics()         {             var go = new GameObject();             go.AddComponent<Rigidbody>();             var originalPosition = go.transform.position.y;              yield return new WaitForFixedUpdate();              Assert.AreNotEqual(originalPosition, go.transform.position.y);         }           [UnityTest]         public IEnumerator LobbySceneSetUp()         {             LoadLobbyScene(this);             yield return null;         }          [UnityTest]         public IEnumerator HostButton()         {             bool host = Input.GetButton("HostButton");             yield return new WaitForSeconds(1);             Assert.AreEqual(host, false);         }          [UnityTest]         public IEnumerator JoinButton()         {             bool join = Input.GetButton("JoinButton");             yield return new WaitForSeconds(1);             Assert.AreEqual(join, false);         }          public static void LoadLobbyScene(PlayModeTests instance)         {             SceneManager.LoadScene("LobbyScene", LoadSceneMode.Single);         }     } }   